import React, { useState } from 'react';
import { TransitionGroup, CSSTransition } from "react-transition-group";
import { Link } from 'react-router-dom';
import { Drawer, Badge } from 'antd';
import styles from './header.module.css';
import globalstyle from '../../style.module.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faShoppingCart,faSquare, faBars  } from '@fortawesome/free-solid-svg-icons';
import logo from '../../assets/img/logospazainv.png';
import cx from 'classnames'
import ScreenSize from '../settings/screenSize';

// import { Navbar, NavDropdown, Nav } from 'react-bootstrap';
// import Cart from '../cart/cart';
import CartContainer from '../../containers/cart/cartContainer';
import SideBar from './sideBar';
// import Sidebar from '../sidebar/sidebar';

const Topbar = (props) => {

     const [ visibility, setVisible ] = useState(false);
     const [ placement ] = useState('left');

     const showDrawer = () => {
          setVisible(true);
     };

     const onClose = () => {
          setVisible(false);
     };




const width = ScreenSize();

const [showCart, setShowCart] = useState(false);
const [showHowToSell, setShowHowToSell] = useState(false);



return (
<>
<div className={styles.nav} >
<div className={globalstyle.container}>
<span className={ cx(styles.showSideNav, styles.navbarLeft) }   onClick={showDrawer}> 
<FontAwesomeIcon icon={faBars} /> 
</span>



<Drawer
          title="Basic Drawer"
          placement={placement}
          closable={false}
          onClose={onClose}
          visible={visibility}
          key={placement}
        >

<SideBar />
</Drawer>





{ (width < 700) ?
<ul className={ cx(globalstyle.topLogoBorder, styles.navbarLeft) } >
<li><Link to="/"><img className={globalstyle.topLogo} src={logo} alt="Yospaza.com" /></Link></li>
</ul> : null
}
     {/* <ul className={styles.navbarLeft}>
     <li className={styles.topBarItem}><Link to="/how-to-sell">Track orders</Link></li>
     <li className={styles.topBarItem}><Link to="/">Free returns</Link></li>
    </ul>  */}
    <ul className={styles.navbarRight}>
    <li onClick={()=>setShowHowToSell(!showHowToSell)} onClick={()=>setShowHowToSell(!showHowToSell)} className={styles.toggleCart}>How To Sell</li>
    <li onClick={()=>setShowCart(!showCart)} className={styles.toggleCart}>   <Badge count={5} offset={[10, 10]}>
    <FontAwesomeIcon icon={faShoppingCart} />
    </Badge></li>
    </ul> 
 { showHowToSell ?  
  <ul className={globalstyle.topBarDropdown}>
              {/* <li className={globalstyle.topBarDropdownText}> How To Sell</li> */}
         <li><Link to="/sellerlogin" className={globalstyle.roundButton}>Start selling</Link></li>
 </ul>
: null }
  </div>
 </div>

 { showCart ? <CartContainer position="dropdown" /> : null }

 </>
);
};

export default Topbar;
